# Sembrar tabla User
# =============================================================================
User.destroy_all
#
500.times do |i|
    nickname = (Faker::Name.first_name_neutral + rand(1000).to_s)
    nombre = nickname
    apellido = Faker::Name.last_name
    correo = nickname + '@' + nickname + '.com'

    User.create!(
        username: nickname,
        name: nombre, 
        last_name: apellido, 
        email: correo, 
        phone: "5690000000#{i}",
        encrypted_password: "123456#{i}",
        user_type_id: rand(1..5),
        login_type_id: rand(1..5),
        plan_id: rand(1..10),
        enabled: booleano[rand(2)],
        confirm_email: booleano[rand(2)],
        registration_date: fecha_hora_rand(),
        discharge_date: fecha_hora_rand(),
        confirmation_email_date: fecha_hora_rand()
    )
end
p "User creados = #{User.count}"

